# Form input
with st.form("data_form"):
    hole = st.text_input("No Hole", 0)
    depth_p = st.text_input("Depth Plan", 5.2)
    depth_a = st.text_input("Depth Actual", 5.2)

    start = st.time_input("Start", datetime.time(7,0))
    finish = st.time_input("Start", datetime.time(19,0))

    rock_type = st.selectbox("Rock Type", ["SOFT", "MEDIUM", "HARD", "RONGGA"])

    road_type = st.selectbox("Basah/Kering", ["BASAH","KERING"])

    Percsn = st.text_input("Percsn", 9)
    Rttion = st.text_input("Rttion", 9)
    Feed = st.text_input("Feed", 9)
    flushing = st.text_input("Flushing", 9)


    submit = st.form_submit_button("Tampilkan")

if submit:
    form = [{
            "HOLE" : hole, 
            "DEPTH PLANT" : depth_p, 
            "DEPTH ACTUAL": depth_a, 
            "START" : start, 
            "FINISH" : finish, 
            "ROCK_TYPE" : rock_type, 
            "BASAH/KERING" : road_type, 
            "PERCSN" : Percsn, 
            "RITTION" : Rttion, 
            "FEED" : Feed, 
            "FLUSHING" : flushing
        }]
    for activity, (x1, y1, x2, y2) in coordinates[no].items():
        c_x = x1
        c_y = (y1 + y2) // 2
        if activity == "TOTAL": continue
        if activity == "REMARKS": continue
        if activity == "SOFT" or activity == "MEDIUM" or activity == "HARD" or activity == "RONGGA":
            if form[0]["ROCK_TYPE"] == "SOFT":
                cv2.putText(image, form[0]["ROCK_TYPE"], (c_x, c_y), cv2.FONT_HERSHEY_PLAIN, 0.8, (0, 255, 0), 2)
                continue
            elif form[0]["ROCK_TYPE"] == "MEDIUM":
                cv2.putText(image, form[0]["ROCK_TYPE"], (c_x, c_y), cv2.FONT_HERSHEY_PLAIN, 0.8, (0, 255, 0), 2)
                continue
            elif form[0]["ROCK_TYPE"] == "HARD":
                cv2.putText(image, form[0]["ROCK_TYPE"], (c_x, c_y), cv2.FONT_HERSHEY_PLAIN, 0.8, (0, 255, 0), 2)
                continue
            elif form[0]["ROCK_TYPE"] == "RONGGA":
                cv2.putText(image, form[0]["ROCK_TYPE"], (c_x, c_y), cv2.FONT_HERSHEY_PLAIN, 0.8, (0, 255, 0), 2)
                continue

        cv2.putText(image, str(form[0][activity]), (c_x, c_y), cv2.FONT_HERSHEY_PLAIN, 0.8, (0, 255, 0), 2)
    no+=1



